    @Test
    public void IT_TD_STATE_001_002_001(){
        int id=26;
        messageService.confirmMessage(id);
        Message message=messageService.findById(id);
        assertEquals(2,message.getState());
    }
    @Test
    public void IT_TD_STATE_001_002_002() {
        int id=250;
        assertThrows(RuntimeException.class,
                ()->messageService.confirmMessage(id));
    }
