    @Test
    public void IT_TD_001_009_001_001() {
        Pageable pageable= PageRequest.of(0,100, Sort.by("time").descending());
        List<Message> messages=messageService.findByUser("test",pageable).getContent();
        for(Message i:messages){
            assertEquals("test",i.getUserID());
        }
    }
    @Test
    public void IT_TD_001_009_001_002() {
        Pageable pageable= PageRequest.of(0,100, Sort.by("time").descending());
        List<Message> messages=messageService.findByUser("不存在用户",pageable).getContent();
        for(Message i:messages){
            assertEquals("不存在用户",i.getUserID());
        }
    }


